import { useState } from "react";
import axios from "axios";

export default function GameplayClusteringUi() {
  const [file, setFile] = useState(null);
  const [isLabeled, setIsLabeled] = useState(false);
  const [gameName, setGameName] = useState("");
  const [result, setResult] = useState(null);
  const [visualization, setVisualization] = useState(null);
  const [progress, setProgress] = useState(0);
  const [uploading, setUploading] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const updateClusterImage = () => {
    const timestamp = new Date().getTime(); // Unique timestamp to prevent caching
    setVisualization(`http://127.0.0.1:8000/clusters.png?cacheBust=${timestamp}`);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      alert("Please upload a video file.");
      return;
    }

    const formData = new FormData();
    formData.append("video", file);
    formData.append("is_labeled", isLabeled);
    if (isLabeled) {
      formData.append("game_name", gameName);
    }

    setUploading(true);
    setProgress(0);

    try {
      const response = await axios.post("http://127.0.0.1:8000/upload/", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        onUploadProgress: (progressEvent) => {
          const percent = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percent);
        },
      });

      setUploading(false);
      setResult(response.data);
      updateClusterImage(); // Update clustering visualization after upload
    } catch (error) {
      console.error("Error uploading video:", error);
      alert("Upload failed.");
      setUploading(false);
    }
  };

  return (
    <div className="p-6 max-w-md mx-auto bg-white rounded-xl shadow-md space-y-4">
      <h1 className="text-xl font-bold">Gameplay Clustering</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="file" accept="video/*" onChange={handleFileChange} className="block w-full" />
        <label className="flex items-center space-x-2">
          <input type="checkbox" checked={isLabeled} onChange={() => setIsLabeled(!isLabeled)} />
          <span>Is this a labeled video?</span>
        </label>
        {isLabeled && (
          <input
            type="text"
            placeholder="Enter Game Name"
            value={gameName}
            onChange={(e) => setGameName(e.target.value)}
            className="block w-full p-2 border rounded"
          />
        )}
        <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">
          Submit
        </button>
      </form>

      {uploading && (
        <div className="w-full bg-gray-200 rounded h-6 mt-4">
          <div
            className="bg-blue-600 h-6 rounded"
            style={{ width: `${progress}%` }}
          ></div>
          <p className="text-center mt-1">{progress}%</p>
        </div>
      )}

      {result && (
        <div className="p-4 bg-gray-100 rounded-md">
          <p><strong>Result:</strong> {result.message || `Game: ${result.game} (Distance: ${result.distance})`}</p>
          {visualization && <img src={visualization} alt="Clustering Visualization" className="mt-2" />}
        </div>
      )}
    </div>
  );
}
